//CheckoutModal.js file

import React, { useState } from "react";

const CheckoutModal = ({ cartItems, onClose, onCheckoutSubmit }) => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Calculate total using 'price' and 'qty' from App.js's cart object
  const totalAmount = cartItems.reduce( 
    (total, item) => total + item.price * item.qty, 
    0
  );

  const handleSubmit = async () => {
    // 1. Basic validation
    if (name.trim() === "" || phone.trim() === "") {
      alert("Please enter your name and phone number.");
      return;
    }

    setIsSubmitting(true);

    // 2. THIS IS THE KEY ACTION: Triggers the checkout in App.js
    await onCheckoutSubmit(name, phone); 

    setIsSubmitting(false);
  };

  // NOTE: Styling is simplified/inline here since we are relying on a mix of 
  // your external CSS classes and basic inline styles for the modal.
  return (
    // Uses the CSS class 'modal' from your styles.css
    <div className="modal"> 
      <div className="modal-card">
        <> 
          <h2 style={{textAlign: 'center', marginBottom: '15px', fontSize: '1.5rem', fontWeight: 'bold', color: '#111'}}>
            Checkout
          </h2>
          <div style={{marginBottom: '10px'}}>
            <input
              type="text"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <input
              type="tel"
              placeholder="Enter your phone number"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
            />
          </div>

          <div style={{textAlign: 'center', marginTop: '10px', fontWeight: 'bold', color: '#111'}}>
              Total Payable: ₹{Math.round(totalAmount)}
          </div>

          <div style={{marginTop: '20px', display: 'flex', justifyContent: 'space-between'}}>
            <button
              onClick={onClose}
              style={{ background: '#ddd', color: '#111' }}
              disabled={isSubmitting}
            >
              Cancel
            </button>
            <button
              // ✅ FIX: The onClick handler correctly calls handleSubmit
              onClick={handleSubmit} 
              disabled={isSubmitting}
            >
              {isSubmitting ? "Generating Bill..." : "Generate Bill"}
            </button>
          </div>
        </>
      </div>
    </div>
  );
};

export default CheckoutModal;
