import React, { useState } from "react";

// The CheckoutModal component now correctly receives:
// 1. cartItems (the list of items)
// 2. onClose (the function to close the modal)
// 3. onCheckoutSubmit (the handleCheckout function from App.js)
const CheckoutModal = ({ cartItems, onClose, onCheckoutSubmit }) => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Calculate total using the correct properties: 'price' and 'qty' from App.js's cart object
  const totalAmount = cartItems.reduce( 
    (total, item) => total + item.price * item.qty, 
    0
  );

  const handleSubmit = async () => {
    // 1. Basic validation
    if (name.trim() === "" || phone.trim() === "") {
      alert("Please enter your name and phone number.");
      return;
    }

    setIsSubmitting(true);

    // 2. Call the main checkout function from App.js
    // This is the key step: It sends name and phone to handleCheckout -> checkout API -> clears cart -> shows receipt.
    await onCheckoutSubmit(name, phone); 

    // The App.js logic handles closing the modal and setting the receipt.
    setIsSubmitting(false);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50">
      <div className="bg-white rounded-2xl p-6 w-[90%] max-w-md relative shadow-xl">
        <> 
          <h2 className="text-2xl font-semibold text-gray-800 mb-4 text-center">
            Checkout
          </h2>
          <div className="space-y-3">
            <input
              type="text"
              placeholder="Enter your name"
              className="w-full border border-gray-300 p-2 rounded-md"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <input
              type="tel"
              placeholder="Enter your phone number"
              className="w-full border border-gray-300 p-2 rounded-md"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
            />
          </div>

          <div className="mt-5 text-lg font-bold text-gray-800 text-center">
              Total Payable: â‚¹{Math.round(totalAmount)}
          </div>

          <div className="mt-5 flex justify-between">
            <button
              onClick={onClose}
              className="px-4 py-2 bg-gray-400 text-white rounded-lg hover:bg-gray-500"
              disabled={isSubmitting}
            >
              Cancel
            </button>
            <button
              // This onClick handler is the critical fix.
              onClick={handleSubmit} 
              className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:bg-green-400"
              disabled={isSubmitting}
            >
              {isSubmitting ? "Generating Bill..." : "Generate Bill"}
            </button>
          </div>
        </>
      </div>
    </div>
  );
};

export default CheckoutModal;
