import React from "react";

export default function Cart({ cartItems, onRemove, onCheckout, onClose }) {
  const totalPrice = cartItems.reduce((sum, item) => sum + item.price, 0);

  return (
    <div
      style={{
        backgroundColor: "white",
        padding: "20px",
        borderRadius: "12px",
        width: "90%",
        maxWidth: "400px",
        maxHeight: "80vh",
        overflowY: "auto",
        color: "#222",
        position: "relative",
        boxShadow: "0 4px 12px rgba(0,0,0,0.3)",
      }}
    >
      <button
        onClick={onClose}
        style={{
          position: "absolute",
          top: "10px",
          right: "10px",
          background: "none",
          border: "none",
          fontSize: "20px",
          cursor: "pointer",
          color: "#555",
        }}
      >
        âœ–
      </button>

      <h2 style={{ textAlign: "center", marginBottom: "15px" }}>ðŸ›’ Your Cart</h2>

      {cartItems.length === 0 ? (
        <p style={{ textAlign: "center" }}>Your cart is empty.</p>
      ) : (
        cartItems.map((item, index) => (
          <div
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              marginBottom: "10px",
              borderBottom: "1px solid #ddd",
              paddingBottom: "6px",
            }}
          >
            <p style={{ margin: 0 }}>
              {item.name} - â‚¹{item.price}
            </p>
            <button
              onClick={() => onRemove(item)}
              style={{
                backgroundColor: "red",
                color: "white",
                border: "none",
                padding: "4px 8px",
                borderRadius: "6px",
                cursor: "pointer",
                fontSize: "12px",
              }}
            >
              Remove
            </button>
          </div>
        ))
      )}

      {cartItems.length > 0 && (
        <div style={{ textAlign: "center", marginTop: "15px" }}>
          <h3>Total: â‚¹{totalPrice}</h3>
          <button
            onClick={onCheckout}
            style={{
              backgroundColor: "#00bcd4",
              color: "white",
              border: "none",
              padding: "10px 20px",
              borderRadius: "8px",
              fontSize: "16px",
              cursor: "pointer",
              marginTop: "10px",
            }}
          >
            Checkout
          </button>
        </div>
      )}
    </div>
  );
}
