//CardView file 
import React from "react";

// The entire 'styles' object and all 'style' props have been removed.
// This component now relies solely on the classes defined in your CSS.
export default function CartView({ items, total, onRemove, onQty, onCheckout, onClose }) {
  return (
    <div className="cart-view"> 
      {/* Close Button - using a CSS class instead of inline style */}
      <button onClick={onClose} className="close-btn">
        âœ•
      </button>

      <h2>ðŸ›’ Your Cart</h2>
      {items.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          {items.map((item) => (
            <div key={item.id} className="cart-item">
              <div className="item-info">
                <strong>{item.name}</strong>
                <span>â‚¹{Math.round(item.price)}</span>
              </div>

              <div className="item-actions">
                <button onClick={() => onQty(item.id, item.qty - 1)}>-</button>
                <span>{item.qty}</span>
                <button onClick={() => onQty(item.id, item.qty + 1)}>+</button>
                <button className="remove-btn" onClick={() => onRemove(item.id)}>
                  Remove
                </button>
              </div>
            </div>
          ))}

          <div className="cart-total">
            <strong>Total: â‚¹{Math.round(total)}</strong>
          </div>
          <button className="checkout-btn" onClick={onCheckout}>
            Checkout
          </button>
        </>
      )}
    </div>
  );
}
// The 'styles' constant at the bottom of the file is removed.
