import React, { useState } from "react";


const CheckoutModal = ({ cartItems, onClose, onCheckoutSubmit }) => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Calculate total using 'price' and 'qty' from App.js's cart object
  const totalAmount = cartItems.reduce( 
    (total, item) => total + item.price * item.qty, 
    0
  );

  const handleSubmit = async () => {
    // Basic validation
    if (name.trim() === "" || phone.trim() === "") {
      alert("Please enter your name and phone number.");
      return;
    }

    setIsSubmitting(true);

    // Call the external handler from App.js
    await onCheckoutSubmit(name, phone); 

    // App.js handles closing the modal and showing the receipt
    setIsSubmitting(false);
  };

  return (
    // Uses the CSS classes provided in your styles.css for modals
    <div className="modal"> 
      <div className="modal-card">
        <> 
          <h2 className="text-2xl font-semibold text-gray-800 mb-4 text-center">
            Checkout
          </h2>
          <div className="space-y-3">
            <input
              type="text"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <input
              type="tel"
              placeholder="Enter your phone number"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
            />
          </div>

          <div style={{textAlign: 'center', marginTop: '10px'}}>
              <strong>Total Payable: â‚¹{Math.round(totalAmount)}</strong>
          </div>

          <div style={{marginTop: '20px', display: 'flex', justifyContent: 'space-between'}}>
            <button
              onClick={onClose}
              style={{ background: '#ddd', color: '#111' }}
              disabled={isSubmitting}
            >
              Cancel
            </button>
            <button
              onClick={handleSubmit} 
              disabled={isSubmitting}
            >
              {isSubmitting ? "Generating Bill..." : "Generate Bill"}
            </button>
          </div>
        </>
      </div>
    </div>
  );
};

export default CheckoutModal;
