import React, { useState } from "react";
import Cart from "./Cart";

export default function App() {
  const [cartItems, setCartItems] = useState([]);
  const [showCart, setShowCart] = useState(false);

  const products = [
    { id: 1, name: "Dumbbell", price: 1200 },
    { id: 2, name: "Yoga Mat", price: 800 },
    { id: 3, name: "Protein Shaker", price: 450 },
    { id: 4, name: "Resistance Band", price: 600 },
  ];

  const handleAddToCart = (product) => {
    setCartItems((prev) => [...prev, product]);
  };

  const handleRemoveFromCart = (product) => {
    setCartItems((prev) =>
      prev.filter((item, index) => index !== prev.indexOf(product))
    );
  };

  const handleCheckout = () => {
    alert(`‚úÖ Checkout successful! Total ‚Çπ${totalPrice}`);
    setCartItems([]);
    setShowCart(false);
  };

  const totalPrice = cartItems.reduce((sum, item) => sum + item.price, 0);

  return (
    <div
      style={{
        fontFamily: "Poppins, sans-serif",
        padding: "20px",
        textAlign: "center",
        background: "linear-gradient(to bottom, #101010, #1f1f1f)",
        minHeight: "100vh",
        color: "white",
      }}
    >
      <h1 style={{ marginBottom: "20px" }}>üèãÔ∏è Fitness Store</h1>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(160px, 1fr))",
          gap: "15px",
          marginBottom: "80px",
        }}
      >
        {products.map((product) => (
          <div
            key={product.id}
            style={{
              backgroundColor: "#222",
              padding: "15px",
              borderRadius: "12px",
              boxShadow: "0 4px 8px rgba(0,0,0,0.3)",
            }}
          >
            <h3>{product.name}</h3>
            <p>‚Çπ{product.price}</p>
            <button
              onClick={() => handleAddToCart(product)}
              style={{
                backgroundColor: "#00bcd4",
                color: "white",
                border: "none",
                padding: "8px 14px",
                borderRadius: "6px",
                cursor: "pointer",
                fontWeight: "bold",
              }}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      {/* Floating Cart Button */}
      <button
        onClick={() => setShowCart(true)}
        style={{
          position: "fixed",
          bottom: "20px",
          right: "20px",
          backgroundColor: "#00bcd4",
          color: "white",
          border: "none",
          borderRadius: "50%",
          width: "60px",
          height: "60px",
          fontSize: "18px",
          cursor: "pointer",
          boxShadow: "0 0 10px rgba(0,0,0,0.3)",
        }}
      >
        üõí
        <span
          style={{
            position: "absolute",
            top: "8px",
            right: "10px",
            backgroundColor: "red",
            color: "white",
            borderRadius: "50%",
            padding: "2px 6px",
            fontSize: "12px",
          }}
        >
          {cartItems.length}
        </span>
      </button>

      {/* Cart Overlay */}
      {showCart && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.7)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
          }}
        >
          <Cart
            cartItems={cartItems}
            onRemove={handleRemoveFromCart}
            onCheckout={handleCheckout}
            onClose={() => setShowCart(false)}
          />
        </div>
      )}
    </div>
  );
}
