import React from 'react';
export default function CartView({ items, total, onRemove, onQty, onCheckout }) {
  return (
    <aside className="cart">
      <h2>Your Cart</h2>
      {items.length === 0 && <div className="empty">Cart is empty</div>}
      {items.map(it => (
        <div key={it.id} className="cart-item">
          <div className="cart-item-left">
            <div>{it.name}</div>
            <div>₹{Math.round(it.price)}</div>
          </div>
          <div className="cart-item-right">
            <input type="number" min="1" value={it.qty} onChange={e => onQty(it.id, parseInt(e.target.value || '1', 10))} />
            <button onClick={() => onRemove(it.id)}>Remove</button>
          </div>
        </div>
      ))}
      <div className="cart-total">Total: ₹{Math.round(total)}</div>
      <button disabled={items.length===0} onClick={onCheckout}>Checkout</button>
    </aside>
  );
}
