import React from "react";
import {
  Card,
  CardContent,
  Typography,
  Button,
  Divider,
  Box,
  IconButton,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import ShoppingCartCheckoutIcon from "@mui/icons-material/ShoppingCartCheckout";

const CartView = ({ items, total, onRemove, onCheckout }) => {
  return (
    <Box
      sx={{
        p: 3,
        background: "#ffffff",
        borderRadius: 3,
        boxShadow: 3,
        height: "fit-content",
      }}
    >
      <Typography variant="h5" fontWeight="bold" gutterBottom>
        ðŸ›’ Your Cart
      </Typography>

      {items.length === 0 ? (
        <Typography color="text.secondary">Your cart is empty.</Typography>
      ) : (
        <>
          {items.map((item) => (
            <Card
              key={item.id}
              sx={{
                mb: 2,
                borderRadius: 2,
                background: "#f9f9f9",
              }}
            >
              <CardContent
                sx={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                }}
              >
                <Box>
                  <Typography variant="subtitle1" fontWeight="bold">
                    {item.name}
                  </Typography>
                  <Typography color="text.secondary">
                    â‚¹{item.price} Ã— {item.qty}
                  </Typography>
                </Box>
                <IconButton
                  color="error"
                  onClick={() => onRemove(item.id)}
                  size="small"
                >
                  <DeleteIcon />
                </IconButton>
              </CardContent>
            </Card>
          ))}

          <Divider sx={{ my: 2 }} />
          <Typography variant="h6" fontWeight="bold">
            Total: â‚¹{Math.round(total)}
          </Typography>

          <Button
            fullWidth
            variant="contained"
            color="success"
            startIcon={<ShoppingCartCheckoutIcon />}
            sx={{ mt: 2 }}
            onClick={onCheckout}
          >
            Proceed to Checkout
          </Button>
        </>
      )}
    </Box>
  );
};

export default CartView;
