//App.js file 

import React, { useEffect, useState } from "react";
import {
  fetchProducts,
  fetchCart,
  addToCart,
  removeCartItem,
  updateCartItem,
  checkout,
} from "./api";
import ProductList from "./components/ProductList";
import CartView from "./components/CartView";
import CheckoutModal from "./components/CheckoutModal";
import "./styles.css";

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState({ items: [], total: 0 });
  const [showCart, setShowCart] = useState(false);
  const [showCheckout, setShowCheckout] = useState(false);
  const [receipt, setReceipt] = useState(null);

  // Load products and cart
  const loadProducts = async () => setProducts(await fetchProducts());
  const loadCart = async () => setCart(await fetchCart());

  useEffect(() => {
    loadProducts();
    loadCart();
  }, []);

  // Cart Operations
  const handleAdd = async (productId) => {
    await addToCart(productId, 1);
    await loadCart();
  };

  const handleRemove = async (itemId) => {
    await removeCartItem(itemId);
    await loadCart();
  };

  const handleQty = async (itemId, qty) => {
    if (qty < 1) return;
    await updateCartItem(itemId, qty);
    await loadCart();
  };

  // âœ… UPDATED: Now accepts 'name' and 'phone'
  // It calls the API, gets the receipt, closes the modals, and clears the cart via loadCart().
  const handleCheckout = async (name, phone) => { 
    const payload = {
      cartItems: cart.items.map((i) => ({
        productId: i.productId,
        qty: i.qty,
      })),
      name,
      phone, // Passing phone as requested (instead of email)
    };

    // NOTE: The mock 'checkout' API might still internally use 'email' or ignore 'phone'.
    // However, this structure correctly sends the data and triggers the API call.
    const res = await checkout(payload); 

    setReceipt(res.receipt);
    setShowCheckout(false);
    setShowCart(false);
    await loadCart(); // Clears the cart by fetching the (now empty) cart data
  };

  return (
    <div className="app">
      <header className="header">
        <h1>Vibe â€” Mock Shop</h1>
        <div
          className="cart-summary"
          onClick={() => setShowCart(true)}
          style={{ cursor: "pointer" }}
        >
          ðŸ›’ Items: {cart.items.length} â€¢ Total: â‚¹{Math.round(cart.total)}
        </div>
      </header>

      <main className="main">
        <ProductList products={products} onAdd={handleAdd} />
      </main>

      {/* ðŸ›’ Cart Drawer */}
      {showCart && (
        <div
          className="cart-overlay"
          onClick={() => setShowCart(false)} // close if clicking outside
          style={{
            position: "fixed",
            inset: 0,
            background: "rgba(0,0,0,0.5)",
            display: "flex",
            justifyContent: "flex-end",
            zIndex: 1500,
          }}
        >
          <div
            onClick={(e) => e.stopPropagation()} // prevent closing on inside click
          >
            <CartView
              items={cart.items}
              total={cart.total}
              onRemove={handleRemove}
              onQty={handleQty}
              onCheckout={() => setShowCheckout(true)}
              onClose={() => setShowCart(false)}
            />
          </div>
        </div>
      )}

      {/* ðŸ§¾ Checkout Modal */}
      {showCheckout && (
        // âœ… UPDATED: Passing required props to CheckoutModal
        <CheckoutModal
          cartItems={cart.items} // Pass cart items
          onClose={() => setShowCheckout(false)} // Use onClose to match prop in CheckoutModal
          onCheckoutSubmit={handleCheckout} // Pass the checkout handler
        />
      )}

      {/* ðŸ“„ Receipt Modal */}
      {receipt && (
        <div className="receipt-modal">
          <div className="receipt-card">
            <h3>Receipt</h3>
            <div>Receipt ID: {receipt.id}</div>
            <div>Time: {new Date(receipt.timestamp).toLocaleString()}</div>
            <div>Name: {receipt.name}</div>
            {/* Display phone number if it's available in the receipt, otherwise use a placeholder */}
            <div>Phone: {receipt.phone || 'N/A'}</div> 
            <ul>
              {receipt.items.map((it) => (
                <li key={it.productId}>
                  {it.name} x{it.qty} â€” â‚¹{Math.round(it.lineTotal)}
                </li>
              ))}
            </ul>
            <strong>Total: â‚¹{Math.round(receipt.total)}</strong>
            <button onClick={() => setReceipt(null)}>Close</button>
          </div>
        </div>
      )}

      <footer className="footer">
        Built for Vibe Commerce screening â€” mock checkout only.
      </footer>
    </div>
  );
}

export default App;
