import React, { useState } from "react";
import CartView from "./components/CartView";
import Checkout from "./components/Checkout";

export default function App() {
  const [items, setItems] = useState([
    { id: 1, name: "Dumbbell", price: 1200, qty: 1 },
    { id: 2, name: "Yoga Mat", price: 800, qty: 1 },
  ]);

  const [showCart, setShowCart] = useState(true);
  const [showCheckout, setShowCheckout] = useState(false);
  const [receipt, setReceipt] = useState(null);

  const total = items.reduce((acc, item) => acc + item.price * item.qty, 0);

  const handleRemove = (id) => {
    setItems(items.filter((item) => item.id !== id));
  };

  const handleQtyChange = (id, newQty) => {
    if (newQty <= 0) return;
    setItems(
      items.map((item) =>
        item.id === id ? { ...item, qty: newQty } : item
      )
    );
  };

  const handleCheckout = () => {
    if (items.length === 0) {
      alert("Your cart is empty!");
      return;
    }
    setShowCheckout(true);
  };

  const handleConfirmCheckout = (customer) => {
    // Generate a mock receipt
    const newReceipt = {
      id: Math.floor(Math.random() * 1000000),
      name: customer.name,
      email: customer.email,
      items,
      total,
      timestamp: new Date().toLocaleString(),
    };

    setReceipt(newReceipt);
    setItems([]); // clear cart
    setShowCheckout(false);
    setShowCart(false);
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>🏋️ Fitness Store</h1>

      {!showCart && !receipt && (
        <button
          onClick={() => setShowCart(true)}
          style={{
            padding: "10px 15px",
            borderRadius: "8px",
            border: "none",
            background: "#00bcd4",
            color: "white",
            cursor: "pointer",
          }}
        >
          🛒 Open Cart
        </button>
      )}

      {showCart && (
        <CartView
          items={items}
          total={total}
          onRemove={handleRemove}
          onQty={handleQtyChange}
          onCheckout={handleCheckout}
          onClose={() => setShowCart(false)}
        />
      )}

      {/* Checkout Dialog (Material-UI) */}
      <Checkout
        open={showCheckout}
        onClose={() => setShowCheckout(false)}
        onConfirm={handleConfirmCheckout}
        total={total}
      />

      {/* Receipt Section */}
      {receipt && (
        <div
          style={{
            marginTop: 30,
            background: "#f8f8f8",
            padding: 20,
            borderRadius: 10,
            maxWidth: 400,
          }}
        >
          <h3>🧾 Receipt</h3>
          <p>
            <strong>ID:</strong> {receipt.id}
          </p>
          <p>
            <strong>Name:</strong> {receipt.name}
          </p>
          <p>
            <strong>Email:</strong> {receipt.email}
          </p>
          <p>
            <strong>Date:</strong> {receipt.timestamp}
          </p>
          <hr />
          {receipt.items.map((item) => (
            <p key={item.id}>
              {item.name} × {item.qty} = ₹{item.price * item.qty}
            </p>
          ))}
          <hr />
          <h4>Total: ₹{receipt.total}</h4>
          <button
            style={{
              marginTop: 10,
              background: "#00bcd4",
              color: "white",
              border: "none",
              borderRadius: 6,
              padding: "8px 12px",
              cursor: "pointer",
            }}
            onClick={() => setReceipt(null)}
          >
            Close Receipt
          </button>
        </div>
      )}
    </div>
  );
}
