import React, { useEffect, useState } from 'react';
import { fetchProducts, fetchCart, addToCart, removeCartItem, updateCartItem, checkout } from './api';
import ProductList from './components/ProductList';
import CartView from './components/CartView';
import CheckoutModal from './components/CheckoutModal';
import './styles.css';

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState({ items: [], total: 0 });
  const [showCheckout, setShowCheckout] = useState(false);
  const [receipt, setReceipt] = useState(null);

  const loadProducts = async () => setProducts(await fetchProducts());
  const loadCart = async () => setCart(await fetchCart());

  useEffect(() => { loadProducts(); loadCart(); }, []);

  const handleAdd = async (productId) => { await addToCart(productId, 1); await loadCart(); };
  const handleRemove = async (id) => { await removeCartItem(id); await loadCart(); };
  const handleQty = async (id, qty) => { if (qty < 1) return; await updateCartItem(id, qty); await loadCart(); };

  const handleCheckout = async (name, email) => {
    const payload = { cartItems: cart.items.map(i => ({ productId: i.productId, qty: i.qty })), name, email };
    const res = await checkout(payload);
    setReceipt(res.receipt);
    setShowCheckout(false);
    await loadCart();
  };

  return (
    <div className="app">
      <header className="header">
        <h1>Vibe — Mock Shop</h1>
        <div className="cart-summary">Items: {cart.items.length} • Total: ₹{Math.round(cart.total)}</div>
      </header>

      <main className="main">
        <ProductList products={products} onAdd={handleAdd} />
        <CartView items={cart.items} total={cart.total} onRemove={handleRemove} onQty={handleQty} onCheckout={() => setShowCheckout(true)} />
      </main>

      {showCheckout && <CheckoutModal onCancel={() => setShowCheckout(false)} onSubmit={handleCheckout} />}

      {receipt && (
        <div className="receipt-modal">
          <div className="receipt-card">
            <h3>Receipt</h3>
            <div>Receipt ID: {receipt.id}</div>
            <div>Time: {new Date(receipt.timestamp).toLocaleString()}</div>
            <div>Name: {receipt.name}</div>
            <ul>
              {receipt.items.map(it => (
                <li key={it.productId}>{it.name} x{it.qty} — ₹{Math.round(it.lineTotal)}</li>
              ))}
            </ul>
            <strong>Total: ₹{Math.round(receipt.total)}</strong>
            <button onClick={() => setReceipt(null)}>Close</button>
          </div>
        </div>
      )}

      <footer className="footer">Built for Vibe Commerce screening — mock checkout only.</footer>
    </div>
  );
}

export default App;
