import React from "react";
import {
  Box,
  Card,
  CardContent,
  Typography,
  IconButton,
  Button,
  Divider,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";

const CartView = ({ items, total, onRemove, onQty, onCheckout }) => {
  return (
    <Box
      sx={{
        width: "100%",
        maxWidth: 500,
        margin: "40px auto",
        backgroundColor: "#fff",
        borderRadius: 3,
        boxShadow: 3,
        padding: 3,
      }}
    >
      <Typography
        variant="h5"
        fontWeight="bold"
        gutterBottom
        sx={{ textAlign: "center" }}
      >
        ðŸ›’ Your Cart
      </Typography>

      {items.length === 0 ? (
        <Typography align="center" color="text.secondary">
          Your cart is empty.
        </Typography>
      ) : (
        <>
          {items.map((item) => (
            <Card
              key={item.id}
              sx={{
                marginBottom: 2,
                boxShadow: 1,
                borderRadius: 2,
              }}
            >
              <CardContent
                sx={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                }}
              >
                <Box>
                  <Typography fontWeight="bold">{item.name}</Typography>
                  <Typography color="text.secondary">
                    â‚¹{item.price} Ã— {item.qty}
                  </Typography>
                </Box>

                <Box display="flex" alignItems="center" gap={1}>
                  <Button
                    variant="outlined"
                    size="small"
                    onClick={() => onQty(item.id, item.qty - 1)}
                  >
                    âˆ’
                  </Button>
                  <Typography>{item.qty}</Typography>
                  <Button
                    variant="outlined"
                    size="small"
                    onClick={() => onQty(item.id, item.qty + 1)}
                  >
                    +
                  </Button>

                  <IconButton
                    color="error"
                    onClick={() => onRemove(item.id)}
                    size="small"
                  >
                    <DeleteIcon />
                  </IconButton>
                </Box>
              </CardContent>
            </Card>
          ))}

          <Divider sx={{ my: 2 }} />

          <Typography variant="h6" align="center" gutterBottom>
            Total: â‚¹{Math.round(total)}
          </Typography>

          <Button
            variant="contained"
            fullWidth
            color="primary"
            onClick={onCheckout}
          >
            Proceed to Checkout
          </Button>
        </>
      )}
    </Box>
  );
};

export default CartView;
